import { Injectable } from "@angular/core";
import { BehaviorSubject } from "rxjs";
import { Observable } from 'rxjs/internal/Observable';



@Injectable()
export class ClientesService{
    constructor(){}
     public clienteName = new BehaviorSubject<string>('');
     castCliente = this.clienteName.asObservable();

     public clientePrice = new BehaviorSubject<string>('');
     castPrice = this.clientePrice.asObservable();

     public broadCastClienteName(name:string, price:string){
         this.clienteName.next(name);
         this.clientePrice.next(price);
     }

    clientes:Cliente[] = [];

     public sendClientes(name:string, price:string):Observable<Clientes[]>{
         if(name != "" && price != ""){
             this.clientes.push({name: name, price: price});
         }
         return of(this.clientes);
     }

}
