import { Component } from '@angular/core';

@Component({
  selector: 'app-add-cliente',
  templateUrl: './add-cliente.component.html',
  styleUrls: ['./add-cliente.component.css']
})
export class AddClienteComponent {
  clientes:Cliente[] = [ ];
  clienteName:string = "";
  cliPrice:string = "";
  @ViewChild("it1") myNameElem!: ElementRef;
  @ViewChild("it2") myNameElem2!: ElementRef;


  constructor(private clineteService:ClientesService){}

  public addProduct(){    
    this.clienteService.broadCastProductName(this.myNameElem.nativeElement.value, 
                                            this.myNameElem2.nativeElement.value);
    this.clienteService.sendProducts(this.myNameElem.nativeElement.value,
                                      this.myNameElem2.nativeElement.value);
  }
}
